mapper.insert:
	INSERT 
	  INTO AD 
	       (
			 MEDIA
			,PRODUCTION_DATE
			,NOTE
			,CONTENT
			,REG_ADMIN_IDX
	       ) 
	       VALUES 
	       (
	         #{media}
	         ,#{productionDate}
	         ,#{note}
	         ,#{content}
	         ,#{regAdminIdx}
	       )
	
        SELECT LAST_INSERT_ID()

mapper.update:

	UPDATE AD SET 
	
		   MEDIA = #{media}
	       ,PRODUCTION_DATE = #{productionDate}
	       ,NOTE = #{note}
	       ,CONTENT = #{content}
	       ,MODI_DATE = NOW()
	       ,MODI_ADMIN_IDX = #{modiAdminIdx}
	       
	 WHERE IDX = #{idx}

mapper.select:

	SELECT * FROM AD
	
	WHERE IDX = #{idx}
	AND DEL_YN='N'

mapper.update:

	UPDATE AD SET
	 
		DEL_YN='Y'
		,DEL_DATE = NOW()
		,DEL_ADMIN_IDX = ${delAdminIdx}
		 
	WHERE IDX = ${idx}
