mapper.insert:
	INSERT 
	  INTO EXHIBITION 
	       (
			 EXHIBITION_NAME
			,EXHIBITION_DATE
			,REGION
			,CONTENT
			,REG_ADMIN_IDX
	       ) 
	       VALUES 
	       (
	         #{exhibitionName}
	         ,#{exhibitionDate}
	         ,#{region}
	         ,#{content}
	         ,#{regAdminIdx}
	       )
	
        SELECT LAST_INSERT_ID()

mapper.update:

	UPDATE EXHIBITION SET 
	
		   EXHIBITION_NAME = #{exhibitionName}
	       ,EXHIBITION_DATE = #{exhibitionDate}
	       ,REGION = #{region}
	       ,CONTENT = #{content}
	       ,MODI_DATE = NOW()
	       ,MODI_ADMIN_IDX = #{modiAdminIdx}
	       
	 WHERE IDX = #{idx}

mapper.select:

	SELECT * FROM EXHIBITION
	
	WHERE IDX = #{idx}
	AND DEL_YN='N'

mapper.update:

	UPDATE EXHIBITION SET
	 
		DEL_YN='Y'
		,DEL_DATE = NOW()
		,DEL_ADMIN_IDX = ${delAdminIdx}
		 
	WHERE IDX = ${idx}
