mapper.insert:
	INSERT 
	  INTO PRODUCT_CASE 
	       (
			 VENDOR
			,DELIVER_DATE
			,CONTENT
			,REG_ADMIN_IDX
	       ) 
	       VALUES 
	       (
	         #{vendor}
	         ,#{deliverDate}
	         ,#{content}
	         ,#{regAdminIdx}
	       )
	
        SELECT LAST_INSERT_ID()

mapper.update:

	UPDATE PRODUCT_CASE SET 
	
		   VENDOR = #{vendor}
	       ,DELIVER_DATE = #{deliverDate}
	       ,CONTENT = #{content}
	       ,MODI_DATE = NOW()
	       ,MODI_ADMIN_IDX = #{modiAdminIdx}
	       
	 WHERE IDX = #{idx}

mapper.select:

	SELECT * FROM PRODUCT_CASE
	
	WHERE IDX = #{idx}
	AND DEL_YN='N'

mapper.update:

	UPDATE PRODUCT_CASE SET
	 
		DEL_YN='Y'
		,DEL_DATE = NOW()
		,DEL_ADMIN_IDX = ${delAdminIdx}
		 
	WHERE IDX = ${idx}
